params.query:
-- Create conversations table
CREATE TABLE IF NOT EXISTS conversations (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  participant1_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  participant2_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  last_message TEXT,
  last_message_at TIMESTAMPTZ DEFAULT NOW(),
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW(),
  CHECK (participant1_id < participant2_id),
  UNIQUE(participant1_id, participant2_id)
);

-- Create messages table
CREATE TABLE IF NOT EXISTS messages (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  conversation_id UUID REFERENCES conversations(id) ON DELETE CASCADE NOT NULL,
  sender_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  content TEXT NOT NULL,
  message_type TEXT CHECK (message_type IN ('text', 'voice', 'file', 'image')) DEFAULT 'text',
  file_url TEXT,
  file_name TEXT,
  file_size INTEGER,
  duration INTEGER,
  is_read BOOLEAN DEFAULT FALSE,
  created_at TIMESTAMPTZ DEFAULT NOW()
);

-- Create trade_requests table
CREATE TABLE IF NOT EXISTS trade_requests (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  sender_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  receiver_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  skill_to_teach_id UUID REFERENCES skills(id) ON DELETE CASCADE NOT NULL,
  skill_to_learn_id UUID REFERENCES skills(id) ON DELETE CASCADE NOT NULL,
  message TEXT,
  status TEXT CHECK (status IN ('pending', 'accepted', 'rejected', 'cancelled')) DEFAULT 'pending',
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Create sessions table
CREATE TABLE IF NOT EXISTS sessions (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  trade_request_id UUID REFERENCES trade_requests(id) ON DELETE CASCADE,
  teacher_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  student_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  skill_id UUID REFERENCES skills(id) ON DELETE CASCADE NOT NULL,
  scheduled_at TIMESTAMPTZ NOT NULL,
  duration_minutes INTEGER DEFAULT 60,
  status TEXT CHECK (status IN ('scheduled', 'completed', 'cancelled', 'no_show')) DEFAULT 'scheduled',
  notes TEXT,
  rating INTEGER CHECK (rating >= 1 AND rating <= 5),
  review TEXT,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Enable RLS
ALTER TABLE conversations ENABLE ROW LEVEL SECURITY;
ALTER TABLE messages ENABLE ROW LEVEL SECURITY;
ALTER TABLE trade_requests ENABLE ROW LEVEL SECURITY;
ALTER TABLE sessions ENABLE ROW LEVEL SECURITY;

-- RLS Policies for conversations
CREATE POLICY "Users can view their own conversations" ON conversations
  FOR SELECT TO authenticated
  USING (
    (SELECT auth.uid()) = participant1_id OR 
    (SELECT auth.uid()) = participant2_id
  );

CREATE POLICY "Users can create conversations" ON conversations
  FOR INSERT TO authenticated
  WITH CHECK (
    (SELECT auth.uid()) = participant1_id OR 
    (SELECT auth.uid()) = participant2_id
  );

CREATE POLICY "Users can update their own conversations" ON conversations
  FOR UPDATE TO authenticated
  USING (
    (SELECT auth.uid()) = participant1_id OR 
    (SELECT auth.uid()) = participant2_id
  )
  WITH CHECK (
    (SELECT auth.uid()) = participant1_id OR 
    (SELECT auth.uid()) = participant2_id
  );

-- RLS Policies for messages
CREATE POLICY "Users can view messages in their conversations" ON messages
  FOR SELECT TO authenticated
  USING (
    EXISTS (
      SELECT 1 FROM conversations
      WHERE conversations.id = messages.conversation_id
      AND (
        conversations.participant1_id = (SELECT auth.uid()) OR
        conversations.participant2_id = (SELECT auth.uid())
      )
    )
  );

CREATE POLICY "Users can send messages in their conversations" ON messages
  FOR INSERT TO authenticated
  WITH CHECK (
    (SELECT auth.uid()) = sender_id AND
    EXISTS (
      SELECT 1 FROM conversations
      WHERE conversations.id = messages.conversation_id
      AND (
        conversations.participant1_id = (SELECT auth.uid()) OR
        conversations.participant2_id = (SELECT auth.uid())
      )
    )
  );

CREATE POLICY "Users can update their own messages" ON messages
  FOR UPDATE TO authenticated
  USING ((SELECT auth.uid()) = sender_id)
  WITH CHECK ((SELECT auth.uid()) = sender_id);

-- RLS Policies for trade_requests
CREATE POLICY "Users can view trade requests they sent or received" ON trade_requests
  FOR SELECT TO authenticated
  USING (
    (SELECT auth.uid()) = sender_id OR 
    (SELECT auth.uid()) = receiver_id
  );

CREATE POLICY "Users can create trade requests" ON trade_requests
  FOR INSERT TO authenticated
  WITH CHECK ((SELECT auth.uid()) = sender_id);

CREATE POLICY "Users can update trade requests they received" ON trade_requests
  FOR UPDATE TO authenticated
  USING ((SELECT auth.uid()) = receiver_id OR (SELECT auth.uid()) = sender_id)
  WITH CHECK ((SELECT auth.uid()) = receiver_id OR (SELECT auth.uid()) = sender_id);

-- RLS Policies for sessions
CREATE POLICY "Users can view their own sessions" ON sessions
  FOR SELECT TO authenticated
  USING (
    (SELECT auth.uid()) = teacher_id OR 
    (SELECT auth.uid()) = student_id
  );

CREATE POLICY "Users can create sessions" ON sessions
  FOR INSERT TO authenticated
  WITH CHECK (
    (SELECT auth.uid()) = teacher_id OR 
    (SELECT auth.uid()) = student_id
  );

CREATE POLICY "Users can update their own sessions" ON sessions
  FOR UPDATE TO authenticated
  USING (
    (SELECT auth.uid()) = teacher_id OR 
    (SELECT auth.uid()) = student_id
  )
  WITH CHECK (
    (SELECT auth.uid()) = teacher_id OR 
    (SELECT auth.uid()) = student_id
  );

-- Create indexes for performance
CREATE INDEX idx_conversations_participant1 ON conversations(participant1_id);
CREATE INDEX idx_conversations_participant2 ON conversations(participant2_id);
CREATE INDEX idx_conversations_last_message_at ON conversations(last_message_at DESC);
CREATE INDEX idx_messages_conversation_id ON messages(conversation_id);
CREATE INDEX idx_messages_sender_id ON messages(sender_id);
CREATE INDEX idx_messages_created_at ON messages(created_at DESC);
CREATE INDEX idx_trade_requests_sender_id ON trade_requests(sender_id);
CREATE INDEX idx_trade_requests_receiver_id ON trade_requests(receiver_id);
CREATE INDEX idx_trade_requests_status ON trade_requests(status);
CREATE INDEX idx_sessions_teacher_id ON sessions(teacher_id);
CREATE INDEX idx_sessions_student_id ON sessions(student_id);
CREATE INDEX idx_sessions_scheduled_at ON sessions(scheduled_at);
